import { useEffect, useState } from 'react';

import Header from './Components/Header/Header';
import BottomMenu from './Components/BottomMenu/BottomMenu';
import Banner from './Components/Banner/Banner';
import DishContainer from './Components/DishContainer/DishContainer';
import DebitCard from './Components/DebitCard/DebitCard';

import { Items } from './Components/Data/Data';

import './Styles/App.css';

function App() {
  // Estado do Prato Principal
  const [isMainData, setMainData] = useState(
    Items.filter((element) => element.itemId === "buger01")
  )

  useEffect(() => {
    // Obter elemento da tag li que está dentro de menu
    const menuLi = document.querySelectorAll("#menu li")

    function setMenuActive() {
      menuLi.forEach((n) => n.classList.remove("active"));
      this.classList.add("active");
    }

    // Para cada elemento do menu de loop
    menuLi.forEach(n => n.addEventListener("click", setMenuActive))

    // Menucard Active toggleIcon
    const menuCards = document.querySelector(".rowContainer").querySelectorAll(".rowMenuCard")

    function setMenuCardActive() {
      menuCards.forEach((n) => n.classList.remove("active"));
      this.classList.add("active");
    }

    menuCards.forEach(n => n.addEventListener("click", setMenuCardActive))
  }, [isMainData])

  return (
    <div className="App">
      <Header />

      {/* Main Container */}
      <main>
        <div className="mainContainer">
          <Banner />
          <DishContainer
            isMainData={isMainData}
            setMainData={setMainData}
            Items={Items}
          />
        </div>
        <div className="rightMenu">
          <div className="debitCardContainer">
            <div className="debitCard">
              <DebitCard />
            </div>
          </div>
        </div>
      </main>

      <BottomMenu />
    </div>
  );
}

export default App;
